mapper.select:
select a.fileIdx ,a.fileSeq ,a.fileName ,a.fileRealName ,a.fileSize ,a.board_idx ,a.id ,a.folderPath ,a.del_yn ,a.open_yn ,a.down_yn ,a.fileDescription ,a.ins_dt ,a.upt_dt ,(select count(1) from file_recommend where fileIdx = a.fileIdx and recommendYN = 'Y') as recommendYcnt ,(select count(1) from file_recommend where fileIdx = a.fileIdx and recommendYN = 'N') as recommendNcnt ,(select recommendYN from file_recommend where fileIdx = a.fileIdx and id = #{id}) as recommendYN from file_house a
and NAME = #{keyWord, jdbcType=VARCHAR}
and title = #{keyWord, jdbcType=VARCHAR}
order by fileSeq desc

      
		    select count(1) as cnt
		    from file_house
		 
		
		   
				
					and NAME = #{keyWord, jdbcType=VARCHAR}
				
				
					and title = #{keyWord, jdbcType=VARCHAR}
				
				
					and content = #{keyWord, jdbcType=VARCHAR}

      
		    select folderName
		    from file_folder
		    where id = #{id}

mapper.delete:

	delete from file_house 
	where id = #{id}
	and folderPath = #{folderPath}


	delete from file_recommend 
	where id = #{id}
	and fileIdx = #{fileIdx}
